import React, { createContext } from 'react'
import uuid from 'uuid/v4'

export const BookContext = createContext()

const BookContextProvider = (props) => {
    const [books, setBooks] = useState([
        {title: 'Harry Potter', author: 'J.K Rowling', id:1},
        {title: 'Lord of the Rings', author: 'J.R.R Tolkien', id:2},
        {title: 'The Alchemist', author: 'Paulo Coelho', id:3},
    ])

    const addBook = (title, author) => {
        setBooks([...books, {title: title, author: author, id: uuid()}])
    }

    const removeBook = (id) => {
        setBooks(books.filter(book => {
            if(book.id !== id) return true
            else return false
        }))
    }

    return (
        <BookContext.Provider value={{books: books, addBook: addBook, removeBook: removeBook}}>
            {props.children}
        </BookContext.Provider>
    )
}
